function gp = GP(site)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% FIND THE LOCATION OF ANY SITE LISTED.%%input = site name.%%output% gp = [lat lon elev]%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%reynolds 010303% test% clear% site = 'bnl';  nargin = 1;global gpinfo%DETERMINE THE CURRENT DRIVE NAMEs = cd;i = findstr(s,filesep);drive = s(1:i-1);% THE PATH TO THE RMRTOOLS FOLDERp = fullfile(drive,'matlab','rmrtools','GP.txt');% FILL GP INFO MATRIX IF NECESSARYif length(gpinfo) == 0	fprintf('\nOpen the GP gpinfomation file...\n');	eval(['f = fopen(''' p ''',''r'');']);	% skip the first six lines	for i = 1:6		str = fgetl(f);	end	% get the next line and begin recording	str = fgetl(f);	gpinfo = str;	while feof(f) <= 0		str = fgetl(f);		gpinfo = str2mat(gpinfo, str);	end	fclose(f);end% SIZE OF THE INFO FILE[r,c] = size(gpinfo);%IF NO SITE IS SPECIFIED, THEN SHOW THE ENTIRE FILEif nargin == 0		fprintf('SELECT A SITE BY NUMBER\n');		for j = 1:r			fprintf('%2d  %s\n',j,deblank(gpinfo(j,:)));		end		j = input('Enter line number: ');		str = deblank(gpinfo(j,:));		% DECODE THE STRING		i = findstr(str,':');		a = sscanf(str(i+2:length(str)),'%f %f %f',3);		lat = a(1);  lon = a(2);  elev = a(3);else	% SCAN FOR THE SPECIFIED SITE	site = upper(site);	iline=[];	for i = 1:r		ix = findstr(gpinfo(i,:),':'); % if the line has a ':' in it		if length(ix) > 0 & ix(1) > 0 & findstr( upper(gpinfo(i,1:ix(1))), site)			ix = findstr(gpinfo(i,:), ':');			e = deblank(gpinfo( i, ix(1)+2:c));			iline = [iline; i];		end	end	% NO SITE FOUND BY THAT NAME	if length(iline) == 0		fprintf('No GP for %s, set to NaN\n');		lat=nan; lon=nan; elev=nan;	% ONE OR MORE ENTRIES FOUND	else		if length(iline) == 1			fprintf('%s\n',deblank(gpinfo(iline,:)));			str = deblank(gpinfo(iline,:));		else			fprintf('SELECT A SITE BY NUMBER\n');			for j = 1:length(iline)				fprintf('%2d  %s\n',j,deblank(gpinfo(iline(j),:)));			end			j = input('Enter line number: ');			str = deblank(gpinfo(iline(j),:));		end		% DECODE THE STRING		i = findstr(str,':');		a = sscanf(str(i+2:length(str)),'%f %f %f',3);		lat = a(1);  lon = a(2);  elev = a(3);	endendgp = [lat lon elev];fclose all;gpinfo = [];return