function MetStickPlot(dt, wspd,wdir,lbl,yposrel,vscle,clr,ax);% function MetStickPlot PLOT A HORIZONTAL LINE AT POSITION ypos%        MetStickPlot(dt, wspd,wdir,lbl,yposrel,vscle,clr,ax);% input: %  dt = datetime%  wspd & wdir are wind speed and direction in met polar coordinates%  lbl = label on left hand side, default = ''%  yposrel = rel y position of the plot, default = 0.5%  vscle = speed equivalent to the full vertical axis, default = 50%  clr = color of the sticks, default = 'k'%  ax = axis handle.  Default = 'gca');% BEFORE CALLING THIS FUNCTION%  Define a figure and axis.%  define a title etc.%  This function will plot a vertical liine of sticks across the plot.% rmr 030603if nargin < 8, ax = gca; endif nargin < 7, clr = 'k'; endif nargin < 6, vscle = 50; endif nargin < 5, yposrel = 0.5; endif nargin < 4, lbl = ' '; end	%yposrel = 0.5;%vscle = 50;[u,v] = metp2v(wspd,wdir);xlim = get(ax,'xlim');ylim = get(ax,'ylim');% stick basexpos = xlim(1) + (dt - dt(1)) .* (xlim(2) - xlim(1)) ./ (dt(end) - dt(1));ypos = yposrel * (ylim(2) - ylim(1)) * ones(length(dt),1);hold onpl = plot([xlim(1) xlim(2)], [ypos ypos],'k');% stick end % scale so 10 m/s corresponds to the vertical dimensionscle = (ylim(2) - ylim(1)) ./ vscle;delx = -u .* scle;dely = -v .* scle;% stick end pointsxend = xpos + delx;yend = ypos + dely;% plot all stickshold onfor i = 1:length(dt),	pl = plot([xpos(i),xend(i)],[ypos(i),yend(i)]);	set(pl,'linewidth',2,'color',clr);endhold offtx = text(0,0,lbl);set(tx,'units','normalized','position',[-0.005,yposrel],'fontweight','bold',...'fontsize',18,'horizontalalignment','right','verticalalignment','middle');set(ax,'xlim',xlim,'ylim',ylim);return