function MonRose(yy,MM,id);fin = sprintf('%s%s%s%02d%02d.mat',id,filesep,id,yy,MM);cmd = sprintf('load %s', fin);fprintf('%s\n',cmd);eval(cmd);mon = str2mat('JAN','FEB','MAR','APR','MAY','JUN','JUL','AUG','SEP','OCT','NOV','DEC');monname = mon(MM,:);str = sprintf('%d %s, Station %s', yy+2000, monname, upper(id));rose(ws,wd,str);picname = sprintf('%s%s%s%02d%02d.eps',id,filesep,id,yy,MM);cmd = sprintf('print %s -depsc', picname);fprintf('%s\n',cmd);eval(cmd);