function [str, history] = SelectDataSet% [str, history] = SelectDataSet%*****************************************************%Select a cruise or data set from a menu.%Output% str = short name of the cruise to use on files%      e.g. 'n99'% history = a str array with information on the data set% reynolds 991210%********************************************************k = menu('Choose a data set',... 'Aerosols99/Indoex99',... 'Nauru99',... 'Mauna Loa 99',... 'Melville 99',... 'PolarSea 99'); switch k	case 1		fprintf('AEROSOLS 99, INDOEX 99\n');		str = 'aero99';		history = 'Dataset = AEROSOLS 99, INDOEX 99';		history = str2mat(history,...		  'Ron Brown cruise from Norfolk to Cape Town then into the Indian Ocean');		history = str2mat(history,...		  'Operator: Aerosol99 = reynolds, Indoex99 = misc sci aides.');		history = str2mat(history,...		  'Dates: Aerosols99: 6 Jan 99 to 10 Feb 99');		history = str2mat(history,...		  'Dates: Indoex99: March and April approx');		history = str2mat(history,'file identifier = aero99');	case 2		fprintf('NAURU 99\n');		str = 'n99';		history = 'Dataset = NAURU 99';		history = str2mat(history,...		  'Ron Brown cruise from Darwin to Nauru I.');		history = str2mat(history,...		  'Operator: Scott Smith');		history = str2mat(history,...		  'Dates: 15 June to 20 July 1999, approx');		history = str2mat(history,'file identifier = n99');	case 3		fprintf('MAUNA LOA OBSERVATORY 99\n');		str = 'mlo99';		history = 'Dataset = MAUNA LOA OBSERVATORY 99';		history = str2mat(history,...		  'Calibration of PRPs and MicroTops on MLO');		history = str2mat(history,...		  'Operator: Smith, Reynolds, Miller');		history = str2mat(history,...		  'Dates: 20 July to 10 Aug 1999 approx');		history = str2mat(history,'file identifier = mlo99');	case 4		fprintf('MELVILLE 99\n');		str = 'mel99';		history = 'Dataset = R/V MELVILLE 99';		history = str2mat(history,...		  'R/V Melville cruise, San Diego to Gulf of California.  SIMBIOS cruise');		history = str2mat(history,...		  'Operator: RSMAS technicians');		history = str2mat(history,...		  'Dates: 1-20 Sept 1999');		history = str2mat(history,'file identifier = mel99');	case 5		fprintf('POLARSEA 99\n');		str='psea99';		history = 'Dataset = CG R/V POLARSEA 99';		history = str2mat(history,...		  'R/V PolarSea cruise to the Bering Sea--RSMAS cruise');		history = str2mat(history,...		  'Operator: RSMAS technicians');		history = str2mat(history,...		  'Dates: ?? 1999');		history = str2mat(history,'file identifier = psea99');		endhistoryreturn