function [Pvapchem] = VapPres(Tcrit,Pcrit,Tbnorm,Temp)% VAPPRES -- VAPOR PRESSURE FOR A CHEMICAL BY THE METHOD OF REIDEL%========================================================% INPUT:  Tcrit -- critical temperature (K)%         Pcrit -- critical pressure (Pa)%         Tbnorm -- normal boiling point (K)%         Temp -- temperature for evaluation% OUTPUT: Pvapchem -- vapor pressure for a chemical%%reynolds 981117%=========================================================if Temp < 150  Temp = Temp + 273.16;endpnorm = 1.00666e5; % Pascalstred = Temp ./ Tcrit;trn = Tbnorm ./ Tcrit;prn = pnorm ./ Pcrit;psi = -35 + 36 ./ trn + 42 .* log(trn) - trn^6;alpha = (0.315.*psi - log(prn)) ./ (0.0838.*psi - log(trn));q = 0.0838 .* (3.758 - alpha);x = (-35 .* q) - (-36 .* q) ./ tred...    + (42.*q + alpha).* log(tred) + (-q) .* tred.^6;Pvapchem = Pcrit .* exp(x); return;