function  ViewSeries(dt,x,y, titlestring)%function  ViewSeries(dt,x,y, titlestring)%==============================================================%   EditSer for a general time series%titlestring is optional%================================================================%in% dt = time series% x = main series, blue% y (optional) = secondary series, red% titlestring = label% GLOBALS USED BY EDITSER%  global MISSING EDFILE SAVESTRING TITLE%031121 rmr Cleaned up presentation, add title%050117 rmr cleand up lines 21-31%060926 rmr fixed some of the cases in s/r calls% SET UP FOR THE CORRECT TIME SERIES%TEST x=psp;  % GLOBALS USED BY EDITSERglobal MISSING EDFILE SAVESTRING TITLEsers=2;EDFILE = 'View.txt';% TITLEif nargin == 2,	TITLE = ''; sers = 2;elseif nargin == 3,	if ischar(y), TITLE = y; sers = 2;	else		TITLE = ''; sers = 3;	endelseif nargin == 4,	TITLE = titlestring; sers = 3;endif sers == 2	EditSer(dt,x);else	EditSer(dt,x,y);endreturn