function str = datestr_rmr(t)%------------------------------------------------------------% function str = datestr_rmr(t)% % Makes a date time string of the form:%    yyyy-MM-dd (jjj) hh:mm:ss% % reynolds 000907% --------------------------------------------------------------% TEST%clear, t = [datenum(1941,4,15,12,0,0); datenum(2000,9,7,14,15,16)];[y,M,d,h,m,s] = datevec(t);[yy,jf] = dt2jdf(t);jd = fix(jf);for i = 1:length(t)	if i == 1		str = sprintf('%4d-%02d-%02d (%03d) %02d:%02d:%02d',...			y(i), M(i), d(i), jd(i), h(i), m(i), round(s(i)));	else					str = str2mat(str,...			sprintf('%4d-%02d-%02d (%03d) %02d:%02d:%02d',...				y(i), M(i), d(i), jd(i), h(i), m(i), round(s(i))));	end			end		