function str = datstr(t)%------------------------------------------------------------% function str = datstr(t)% % Makes a date only string of the form:%    yyyy-MM-dd (jjj)% % reynolds 001110% --------------------------------------------------------------% TEST%clear, t = [datenum(1941,4,15,12,0,0); datenum(2000,9,7,14,15,16)];[y,M,d,h,m,s] = datevec(t+1/864000); % add a bit to help in the roundoffs = round(s);[yy,jf] = dt2jdf(t);jd = fix(jf);for i = 1:length(t)	if i == 1		str = sprintf('%4d-%02d-%02d (%03d)',y(i), M(i), d(i), jd(i));	else					str = str2mat(str,...			sprintf('%4d-%02d-%02d (%03d)',y(i), M(i), d(i), jd(i)));	end		end		