function [yyyy,jdf] = dt2jdf(dt)%DT2JDF -- CONVERT FROM DATENUM TO YEAR AND JULIAN DAY% [yyyy,jdf] = d2tjdf(dt)%==========================================================================%%reynolds 981020%==========================================================================[yyyy,MM,dd,hh,mm,ss] = datevec(dt);jdf = datenum(yyyy,MM,dd) - datenum(yyyy,1,1) + 1;jdf = jdf + hh/24 + mm/1440 + ss/86400;return;