function trackplot_mapc,%=============================================% function trackplot_mapf%%global SERIES LATRANGE LONRANGE% LATRANGE(1),LATRANGE(2) = lat limits% LONRANGE(1),LONRANGE(2) = lat limits%% before calling%global SERIES LATRANGE LONRANGE MAPFIG %LATRANGE=[min(sp.lat),max(sp.lat)]%LONRANGE=[min(sp.lon),max(sp.lon)]% "addpath('/Users/rmr/swmain/matlab/crusty/m_map')"%=============================================global SERIES LATRANGE LONRANGE MAPFIGaddpath('/Users/rmr/Dropbox/swmain/matlab/crusty/m_map')	% LATRANGEx=size(LATRANGE); if x(2)==1, LATRANGE=LATRANGE'; endif LATRANGE(1)>LATRANGE(2), x=LATRANGE(1); LATRANGE(1)=LATRANGE(2); LATRANGE(2)=x; end	% LONRANGEif LONRANGE(1)>LONRANGE(2), x=LONRANGE(1); LONRANGE(1)=LONRANGE(2); LONRANGE(2)=x; endx=size(LONRANGE); if x(2)==1, LONRANGE=LONRANGE'; endLONRANGECheck=CompassCheck(LONRANGE);%% %%%%%%%%%%%%%% % SET SCALE% %%%%%%%%%%%%%D = diff(LATRANGE); %(LATRANGE(2)-LATRANGE(1));if D > 20,	scle = 5;elseif D > 10,	scle = 2;elseif D > 4,	scle = 1;elseif D > 1,	scle = .5;else	scle = .2;end%if LATRANGE(1) >= 0, LATRANGE(1) = LATRANGE(1) - rem(LATRANGE(1),scle); else LATRANGE(1) = LATRANGE(1) - scle - rem(LATRANGE(1),scle); end%if LATRANGE(2) > 0, LATRANGE(2) = LATRANGE(2) + scle - rem(LATRANGE(2),scle); else LATRANGE(2) = LATRANGE(2) - rem(LATRANGE(2),scle); endD = diff(LONRANGECheck); %(LONRANGE(2)-LONRANGE(1));if D > 20,	scle = 5;elseif D > 10,	scle = 2;elseif D > 4,	scle = 1;elseif D > 1,	scle = .5;else	scle = .2;endfprintf('LAT LIMS= [%.5f, %.5f], LON LIMS = [%.5f, %.5f]\n',LATRANGE(1),LATRANGE(2),LONRANGE(1),LONRANGE(2));	%disp('PLOT COASTLINES');MAPFIG=figure('position',[50,50,1200,1000]);set(gcf,'papersize',[9.5 7], 'paperposition',[.25 .25 9 6.5])% projectionm_proj('mercator','longitudes',LONRANGECheck,'latitudes',LATRANGE);%Ioffem_proj('mercator','longitudes',[-80, 10],'latitudes',[LATRANGE(1), LATRANGE(2)]);% draw the coast linem_coast('patch',[.8 .8 .8]);%m_gshhs_h('patch',[.8 .8 .8]);% gridm_grid('fontname','helvetica','fontsize',12,'box','fancy',...	'linewidth',.5, 'linespec','-');set(gcf,'color','w');m_proj('get')return